import {Moralis} from 'moralis'
import {useMoralis} from "react-moralis";
import { useState, useEffect } from "react";
import { getExplorer } from "helpers/networks";
import {Card, Image, Tooltip } from "antd";
import {
    FileSearchOutlined,
    SendOutlined,
    ShoppingCartOutlined,
  } from "@ant-design/icons";
  const { Meta } = Card;

  const styles = {
    NFTs: {
      display: "flex",
      flexWrap: "wrap",
      WebkitBoxPack: "start",
      justifyContent: "flex-start",
      margin: "0 auto",
      maxWidth: "1000px",
      width: "100%",
      gap: "10px",
    },
  };

export default function ListNft() {
    
  let [inputList, setInputList] = useState();



  
  const {  isWeb3Enabled, enableWeb3, isAuthenticated, isWeb3EnableLoading, chainId } = useMoralis();
      // const Web3Api = useMoralisWeb3Api();
      // appId="1sNeRHQBsq5ZihAYmV3tcEVlWqoNe3aQMfR0EwxK"
      // useMoralisWeb3Api(api_key, server_url, {
        


      async function getAsset() {

        console.log("called");
            const options = {
            address: "0x0fb6ef3505b9c52ed39595433a21af9b5fcc4431",
            chain: chainId,
            limit: "40",
          };
        const data = await Moralis.Web3API.token.getAllTokenIds(options);
        // console.log(data);
        data.result.map((nft, index) => {
          fetch(nft.token_uri).then(res => {
            res.json().then((res) => {
              // console.log(res); // this should print the url to console

              
              setInputList(inputList => [inputList, 
                <div key={index}>
                  <Card 
                    hoverable
                    actions={[
                      <Tooltip title="View On Blockexplorer">
                        <FileSearchOutlined
                          onClick={() =>
                            window.open(
                              `${getExplorer(chainId)}address/${
                                res.token_address
                              }`,
                              "_blank",
                            )
                          }
                        />
                      </Tooltip>,
                      <Tooltip title="Transfer NFT">
                        <SendOutlined onClick={() => console.log("clicked")} />
                      </Tooltip>,
                      <Tooltip title="Sell On OpenSea">
                        <ShoppingCartOutlined
                          onClick={() => alert("OPENSEA INTEGRATION COMING!")}
                        />
                      </Tooltip>,
                    ]}
                    style={{ width: 240, border: "2px solid #e7eaf3" }}
                    cover={
                      <Image
                        preview={false}
                        src={res?.image || "error"}
                        fallback="data:image/png;base64,"
                        alt=""
                        style={{ height: "300px" }}
                      />
                    }
                    key={index}
                  >
                    <Meta title={res.name} description={res.token_address} />
                  </Card>
                </div>
              ]);

            });
          });
          index;
        });

      }


  
      useEffect(() => {
        const connectorId = window.localStorage.getItem("connectorId");
        if (isAuthenticated && !isWeb3Enabled && !isWeb3EnableLoading)
          enableWeb3({ provider: connectorId });
          getAsset()
          console.log("yep");
      }, [isAuthenticated, isWeb3Enabled]);
  


      return (
        <div className="text-white" style={{ marginTop: "100px" }}>
          <div>
              <div style={{ padding: "15px", maxWidth: "1030px", width: "100%" }}>
                <h1>ðŸ–¼ Our NFT Collection</h1>
                <div style={styles.NFTs}>
                    {inputList} 
                </div>
              </div>
          </div>
      </div>
      );
}